import {
  HeartOutlined,
  MessageOutlined,
  RetweetOutlined,
} from '@ant-design/icons';
import { Button, Card, Image } from 'antd';

const PostCard = ({ post }) => {
  return (
    <div>
      <Card
        cover={post.Images[0] && <PostImages images={post.Images} />}
        actions={[<RetweetOutlined />, <HeartOutlined />, <MessageOutlined />]}
      >
        <Image />
        <Content />
        <Button />
      </Card>
      <CommnetForm />
      <Comments />
    </div>
  );
};

export default PostCard;
