export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: '제로초',
      },
      content: '첫 번째 게시글 #해시태그 #익스페리스',
      Images: [{}],
      Comments: [
        {
          User: {
            nickname: 'hypercoder',
          },
          content: 'hello world',
        },
      ],
      ImagePaths: [],
      postAdded: false,
    },
  ],
};

const ADD_POST = 'ADD_POST';
export const addPost = {
  type: ADD_POST,
};
const dummyPost = {
  User: {
    id: 1,
    nickname: 'HyperCoder',
  },
  Images: [],
  Comments: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
        postAdded: true,
      };
    default:
      return state;
  }
};

export default reducer;
